import styled from "styled-components";

export const StyledLogin = styled.div`
  display: flex;
  width: 100vw;

  //svg
  .svgContainer {
    flex: 2;
    height: 100vh;
    display: flex;
    align-items: center;
  }

  .svgContainer img {
    width: 80%;
    height: 500px;
  }

  //form
  .loginContainer {
    flex: 1;
  }

  .formWrapper {
    margin: 0 auto;
  }
`;
